"use strict";

var textWrapper = document.querySelector('.main_header');
textWrapper.innerHTML = textWrapper.textContent.replace(/\S/g, "<span class='letter'>$&</span>");

anime.timeline({loop: true})
  .add({
    targets: '.main_header .letter',
    opacity: [0,1],
    easing: "easeInOutQuad",
    duration: 2250,
    delay: (el, i) => 150 * (i+1)
  }).add({
    targets: '.main_header .letter',
    opacity: 0,
    duration: 2000,
    easing: "easeOutExpo",
    delay: 1000
  });

